// Image Lazy Loading - Minified Version
(function(){'use strict';const observerOptions={root:null,rootMargin:'50px',threshold:0.1};const loadImage=(img)=>{img.src=img.dataset.src;img.removeAttribute('data-src');img.classList.add('loaded');};const imageObserver=new IntersectionObserver((entries)=>{entries.forEach(entry=>{if(entry.isIntersecting){loadImage(entry.target);imageObserver.unobserve(entry.target);}});},observerOptions);const initLazyLoading=()=>{const lazyImages=document.querySelectorAll('img[data-src]');if('IntersectionObserver'in window){lazyImages.forEach(img=>imageObserver.observe(img));}else{lazyImages.forEach(loadImage);}};if(document.readyState==='loading'){document.addEventListener('DOMContentLoaded',initLazyLoading);}else{initLazyLoading();}})();